using namespace std;
typedef long long ll;
typedef unsigned long long ull;
const int N = 2000100;
const int mod = (int)pow(10, 9) + 7;
const ll INF = 1LL << 60;
#define rep(i,n) for(ll i=(ll)0;i<(ll)n;++i)
#define ALL(x) x.begin(),x.end()
#define pp pair<ll,ll>
#define ppp pair<ll,pp>
#define fi first
#define se second
#define pb push_back
#define fix(n) fixed<<setprecision(n)
#define itr vector<ll>::iterator
#define print(s) cout<<s<<endl
#define inp(s) cin>>s
ll h, w, k, ans;
ll  fact[N], inv[N];
ll ppow(ll x, ll n) {
	ll ans = 1;
	while (n > 0) {
		if ((n & 1) == 1)ans = ans * x;
		x = x * x;
		n >>= 1;
		x %= mod;
		ans %= mod;
	}
	return ans;
}
void setinv(ll n) {
	fact[0] = 1; inv[0] = 1;
	for (int i = 1; i <= n; i++) {
		fact[i] = (fact[i - 1] * i) % mod;  //階乗を求める
		inv[i] = ppow(fact[i], (ll)mod - 2) % mod; // フェルマーの小定理で逆元を求める
	}
}
ll conv(ll n, ll r) {//nCr
	return fact[n] * inv[r] % mod * inv[n - r] % mod;
}
int main() {
	//fstream out("test.txt");
	cin >> h >> w >> k;
	setinv(h + w);
	ll a, b;
	rep(i, k) {
		cin >> a >> b;
		ans -= conv(a + b, b) * conv(h - a + w - b, h - a);
	}
	ans += conv(h + w, h);
	ans %= mod;
	cout << (ans+mod)%mod << endl;
	return 0;
}
