#include <iostream>
#include <vector>
#include <string>

size_t length(const std::string& str) {
	return str.length();
}

int main() {
	std::vector<int> basho{0,0};
	int houkou = 0;
	std::string cmd;
	std::cin >> cmd;
	for (int n = 0; n < int(length(cmd)); n++) {
		if (cmd[n] == 'R') {
			houkou += 90;
			if (houkou == 360) {
				houkou = 0;
			}
		}
		else if (cmd[n] == 'L') {
			houkou -= 90;
			if (houkou == -90) {
				houkou = 270;
			}
		}
		else if (cmd[n] == 'S') {
			if (houkou == 0) {
				basho.at(0)++;
			}
			else if (houkou == 90) {
				basho.at(1)++;
			}
			else if (houkou == 180) {
				basho.at(0)--;
			}
			else if (houkou == 270) {
				basho.at(1)--;
			}
		}
	}
	std::string answer;
	if (basho.at(0) == 0 && basho.at(1) == 0) {
		answer = "YES";
	}
	else {
		answer = "NO";
	}
	std::cout << answer << std::endl;
}
