#include <iostream>
#include <vector>
#include <algorithm>
int buyMaximumProducts(int n, int k, int price[]){
    std::vector< std::pair<int, int> > v;
    for (int i = 0; i < n; ++i){ 
        v.push_back(std::make_pair(price[i], i + 1));
    }    
    std::sort(v.begin(), v.end());    
    int ans = 0;
    for (int i = 0; i < n; ++i) {
        ans += std::min(v[i].second, k / v[i].first);
        k -= v[i].first * std::min(v[i].second, 
                               (k / v[i].first));
    }
    return ans;
}
int main(){
    int price[60000];
    int n,m;
    std::cin>>n>>m;
    for(int i=0;i<n;i++){
        std::cin>>price[i];
    }
    std::cout << buyMaximumProducts(n, m, price) <<"\n";
    return 0;
}
