#include <iostream>

using ll=long long int;

constexpr int MAX_N=25;

int n;
ll p;

ll a[MAX_N];

int main() {

    std::cin>>n>>p;

    for(int i=0;i<n;++i)
        std::cin>>a[i];

    for(int i=0;i<(1<<n);++i){
        ll res1=1,res2=1;
        for(int j=0;j<n;++j){
            if((i>>j)&1){
                res1*=a[j];
                res1%=p;
            }
            else{
                res2*=a[j];
                res2%=p;
            }
        }
        res1=(res1+p)%p;
        res2=(res2+p)%p;
        if(res1==res2){
            std::cout<<"Yes"<<std::endl;
            return 0;
        }
    }

    std::cout<<"No"<<std::endl;

    return 0;
}
