#include <iostream>
#include <string>

int main(){

	std::string str;

	std::cin >> str;

	int B = 0, W = 0;
	for(auto &c : str){
		if(c == 'B'){
			++B;
		}
		else if(c == 'W'){
			++W;
		}
	}

	if(B < W){
		std::cout << "White wins" << std::endl;
	}
	else if(B > W){
		std::cout << "Black wins" << std::endl;
	}
	else{
		std::cout << "Draw" << std::endl;
	}

	return 0;

}
