#include<iostream>
int main()
{
    int N, A, B, C, D;
    std::cin >> N >> A >> B >> C >> D;
    int E = 0;
    int F = 0;
    if (N%A == 0) {
        E = N / A * B;
    }
    else {
        E= (N / A + 1)*B;
    }
    if (N%C == 0) {
        F = N / C * D;
    }
    else {
        F = (N / C + 1)*D;
    }
    if (F>E) {
        std::cout << E << std::endl;
    }
    else {
        std::cout << F << std::endl;
    }
    
}
