#include <iostream>
#include <string>

int main(){

	std::string str;

	std::getline(std::cin, str);

	for(int i = 0; i < 26; ++i){

		if(str.find("caesar") != std::string::npos){
			break;
		}

		for(auto& c : str){
			if(c >= 'a' && c <= 'z'){
				if(c == 'z') c = 'a';
				else ++c;
			}
		}
	}

	std::cout << str << std::endl;;

	return 0;
}
