#include <iostream>
#include <algorithm>
#include <functional>

constexpr int MAX_N = 1000000;

int N;
long long A[MAX_N], B[MAX_N];

int main(){

	std::cin >> N;

	for(int i = 0; i < N; ++i){
		std::cin >> A[i] >> B[i];
	}

	std::sort(A, A + N);
	std::sort(B, B + N, std::greater<int>());

	long long sum = 0;
	for(int i = 0; i < N; ++i){
		sum += A[i] * B[i];
	}

	std::cout << sum << std::endl;

	return 0;
}
