#include <iostream>
using namespace std;
int n, f;
bool sosu(int n) {
	if (n == 1) return false;
	for (int i = 2; i < n; ++i) {
		if (n%i == 0) return false;
	}
	return true;
}
int main() {
	cin >> n;
	for (int i = 2; i < n; ++i) {
		if (n%i == 0 && sosu(i)) {
			if (sosu(n / i)) {
				f = 1;
				break;
			}
		}
	}
	if (f == 1) cout << "yEs" << endl;
	else cout << "N0" << endl;
	return 0;
}
