#include <iostream>
#include <algorithm>

constexpr int max_m = 10000;

int n, m;

int main(){

	std::cin >> n >> m;

	int min = max_m * max_m;
	for(int i = 0; i < m; ++i){

		int a, b;
		std::cin >> a >> b;

		if(n % a == 0){
			min = std::min(min, b * (n / a));
		}
		else{
			min = std::min(min, b * (n / a + 1));
		}
	}

	std::cout << min << std::endl;

	return 0;
}
