#include <iostream>

constexpr int dx[] = {0, 1, 0, -1};
constexpr int dy[] = {1, 0, -1, 0};

constexpr int INF = 1000000;
constexpr int MAX_HW = 100;

int N, M, H, W, Sh, Sw;
int grass[MAX_HW][MAX_HW] = {};

bool dfs(int h, int w, int n = 0);

int main(){

	std::cin >> H >> W >> N >> M;

	for(int i = 0; i < H; ++i){
		for(int j = 0; j < W; ++j){
			grass[i][j] = INF;
		}
	}

	for(int i = 0; i < H; ++i){
		for(int j = 0; j < W; ++j){

			char c;

			std::cin >> c;

			if(c == 'S'){
				Sh = i, Sw = j;
			}
			else if(c == 'w'){
				grass[i][j] = 0;
			}
		}
	}

	if(dfs(Sh, Sw)){
		std::cout << "Yes" << std::endl;
	}
	else{
		std::cout << "No" << std::endl;
	}

	return 0;
}

bool dfs(int h, int w, int n){

	if(n == N){
		return true;
	}

	for(int i = 0; i < 4; ++i){

		int nh = h + dx[i], nw = w + dy[i];

		if(nh >= 0 && nw >= 0 && nh < H && nw < W && grass[nh][nw] <= n){

			int tmp = grass[nh][nw];

			grass[nh][nw] = n + M;
			if(dfs(nh, nw, n + 1)){
				return true;
			}
			grass[nh][nw] = tmp;
		}
	}

	return false;
}
