#include <iostream>
#include <algorithm>

constexpr int MAX_N = 1000;
constexpr int MAX_M = 1000000;

int N, M;
long long v[MAX_N], c[MAX_N];
long long dp[MAX_M + 1];

int main(){

	std::cin >> N >> M;

	for(int i = 0; i < N; ++i){
		std::cin >> v[i] >> c[i];
	}

	for(int i = 0; i < M; ++i){
		dp[i] = -1;
	}
	dp[0] = 0;

	for(int i = 0; i < N; ++i){
		for(int j = M; j >= 0; --j){
			if(j - c[i] >= 0 && dp[j - c[i]] != -1){
				dp[j] = std::max(dp[j], dp[j - c[i]] + v[i]);
			}
		}
	}

	long long ans = 0;
	for(int i = 0; i <= M; ++i){
		ans = std::max(ans, dp[i]);
	}

	std::cout << ans << std::endl;

	return 0;
}
