#include <iostream>
#include <string>

constexpr int MAX=500010;

int q;

int bit[MAX+1];

void add(int m,int w){

    for(int i=m;i<=MAX;i+=i&(-i))
        bit[i]+=w;
}

int sum(int m){

    int ret=0;
    for(int i=m;i>0;i-=i&(-i))
        ret+=bit[i];

    return ret;
}

int getNum(int x){
    int lb=0,ub=MAX;

    while(ub-lb>1){
        int mid=(lb+ub)/2;
        if(sum(mid)>=x)
            ub=mid;
        else
            lb=mid;
    }

    return ub;
}

int main() {

    std::cin>>q;

    for(int i=0;i<q;++i){

        std::string s;
        int x;

        std::cin>>s>>x;

        if(s=="ADD")
            add(x,1);
        else if(s=="DELETE"){
            if(sum(MAX)>=x)
                add(getNum(x),-1);
        }
        else if(sum(MAX)>=x)
            std::cout<<getNum(x)<<std::endl;
        else
            std::cout<<"Not Exist!"<<std::endl;
    }

    return 0;
}
