#include <iostream>

using ll=long long int;

constexpr ll mod = (ll)1e9+7;

int n;

ll pow(ll x,ll m){
    ll res=1;
    for(ll i=1;i<=m;i*=2){
        if(m&i)
            res=res*x%mod;
        x=x*x%mod;
    }
    return res;
}

int main() {

    std::cin>>n;

    if(n%2)
        std::cout<<(2*pow(3,(n-1)/2))%mod<<std::endl;
    else
        std::cout<<(4*pow(3,(n-2)/2))%mod<<std::endl;

    return 0;
}
