#include "iostream"
#include "string"

using namespace std;

int main() {
	string s;
	cin >> s;
	int b=0, w=0;
	for (int i = 0; i < s.size(); i++) {
		if (s[i] == 'B') b++;
		if (s[i] == 'W') w++;
	}
	if (b > w) cout << "Black wins" << endl;
	if (b < w) cout << "White wins" << endl;
	if (b == w) cout << "Draw" << endl;
}
