#include<iostream>
#include<vector>
#include<algorithm>
#include<functional>
#include<string>
#include<cmath>
#include<utility>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
typedef long long ll;
int main() {
	std::string str;
	int count_b = 0, count_w = 0;
	std::cin >> str;
	rep(i, str.length()) {
		if (str[i] == 'B') {
			count_b++;
		}
		if (str[i] == 'W') {
			count_w++;
		}
	}
	if (count_b > count_w) {
		std::cout << "Black wins" << std::endl;
	}
	else if (count_b == count_w) {
		std::cout << "Draw" << std::endl;
	}
	else {
		std::cout << "White wins" << std::endl;
	}

}
