#include <iostream>

int gcd(int a, int b){
    if(a == 0) return b;
    else return gcd(b % a, a);
}

int lcm(int a, int b){
    return a * b / gcd(a, b);
}

int main(){
    
    int a, b;
    
    std::cin >> a >> b;
    
    std::cout << gcd(a, b) << ' ' << lcm(a, b) << std::endl;
    
    return 0;
}
