#include <iostream>
#include <string>

int main() {
	std::string list;
	std::cin >> list;
	int black = 0;
	int white = 0;
	for (int i = 0; i < list.length(); i++) {
		if (list.at(i) == 'B') {
			black++;
		}
		else if (list.at(i) == 'W') {
			white++;
		}
	}
	if (black == white) {
		std::cout << "Draw" << std::endl;
	}
	else if (black > white) {
		std::cout << "Black wins" << std::endl;
	}
	else if (black < white) {
		std::cout << "White wins" << std::endl;
	}
}
