#include<iostream>
#include<string>

int main() {
	std::string str;
	std::cin >> str;
	int W = 0, B = 0;
	for (int i = 0; i < str.size(); i++) {
		if (str[i] == 'W')W++;
		if (str[i] == 'B')B++;
	}
	if (B > W)std::cout << "Black wins" << std::endl;
	else if (B < W)std::cout << "White wins" << std::endl;
	else std::cout << "Draw" << std::endl;
	return 0;
}
