#include <iostream>
#include<cstring>
int main(){
    std::string a;
    std::cin>>a;
    int white=0;
    int black=0;
    for(int i=0;i<a.length();i++){
        if(a[i]=='B'){
            black++;
        }else if(a[i]=='W'){
            white++;
        }
    }
    if(black==white){
        std::cout<<"Draw"<<std::endl;
    }else if(black>white){
        std::cout<<"Black wins"<<std::endl;
    }else{
        std::cout<<"White wins"<<std::endl;
    }
    }
