#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>

int main(int argc, char **argv)
{
	int b=0, w=0;
	std::string input;
	std::cin >> input;
	for(int i = 0; i < input.size(); i++){
		if(input[i]=='B') b++;
		if(input[i]=='W') w++;
	}
	if(b>w) std::cout << "Black wins" << std::endl;
	if(b<w) std::cout << "White wins" << std::endl;
	if(b==w) std::cout << "Draw" << std::endl;
	return 0;
}
