#include <iostream>
#include <vector>
using namespace std;
int main(void){
    // Your code here!
    char a;
    int B = 0,W = 0;
    
    while(std::cin>>a){
        if(a=='B')B++;
        if(a=='W')W++;
    }
    if(B>W){
        std::cout<<"Black wins"<<std::endl;
    }else if(W>B){
        std::cout<<"White wins"<<std::endl;
    }else{
        std::cout<<"Draw"<<std::endl;
    }
}
