#include "iostream"
#include "string"

using namespace std;

int main() {
	int n, m;
	cin >> n >> m;
	int a[10000], b[10000];
	for (int i = 0; i < m; i++) {
		cin >> a[i];
		cin >> b[i];
	}
	int tmp[10000];
	for (int i = 0; i < 10000; i++) {
		tmp[i] = 0;
	}
	for (int i = 0; i < m; i++) {
		if (n % a[i] == 0) tmp[i] = n / a[i] * b[i];
		else tmp[i] = (n / a[i] + 1) * b[i];
	}
	int ans = tmp[0];
	for (int i = 0; i < m; i++) {
		if (ans > tmp[i]) ans = tmp[i];
	}
	cout << ans << endl;
}
