#include <iostream>
#include <string>
int main() {
	std::string str;
	std::cin >> str;
	int b = 0, w = 0;
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'B')
			++b;
		else if (str[i] == 'W')
			++w;
	}
	if (b > w)
		std::cout << "Black wins" << std::endl; 
	else if (b > w)
		std::cout << "White wins" << std::endl;
	else
		std::cout << "Draw" << std::endl;
	system("pause");
	return 0;
}
