#include<iostream>
#include<string>

std::string str;

char c;

int w=0,b=0;

int main(){
    
    std::cin>>str;
    
    for(int i=0;i<sizeof(str);i++){
        c=str[i];
        if(c=='W')
            w++;
        if(c=='B')
            b++;
    }
    
    if(b>w)
        std::cout<<"Black wins"<<std::endl;
    
    if(b<w)
        std::cout<<"White wins"<<std::endl;
    
    if(b==w)
        std::cout<<"Draw"<<std::endl;
    
    return 0;
    
}
