#include<iostream>
#include<string>

int main() {
	std::string str;
	std::cin >> str;
	int white = 0;
	int black = 0;
	for (int i = 0; i < str.size();i++) {
		if (str[i] == 'W') {
			white++;
		}
		else if (str[i] == 'B') {
			black++;
		}
	}
	if (white < black) {
		std::cout << "Black wins" << std::endl;
	}
	else if (white > black) {
		std::cout << "White wins" << std::endl;
	}
	else {
		std::cout << "Draw" << std::endl;
	}
}
