#include <iostream>
#include <string>
using namespace std;
int main() {
	string koma;
	int W = 0;
	int B = 0;
	cin >> koma;
	for (int i = 0; i < koma.size(); i++) {
		if (koma[i] == 'W') {
			W++;
		}
		else if(koma[i]=='B'){
			B++;
		}
	}
	if (W > B) {
		cout << "White wins" << '\n';
	}
	if (W == B) {
		cout << "Draw" << '\n';
	}
	if (W < B) {
		cout << "Black wins" << '\n';
	}
}
