#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>

int main(int argc, char **argv)
{
	int n=0, m=0, a=0, b=0, total = -1;
	std::cin >> n >> m;
	for(int i = 0; i < m; i++){
		int set = 0;
		std::cin >> a >> b;
		set = n / a;
		if(n % a > 0) set++;
		set *= b;
		if(total == -1 || set < total){
			total = set;
		}
	}
	std::cout << total << std::endl;
	return 0;
}
