#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <iomanip>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int n, m, ans, A[101010], B[101010], C[101010];
string s;
int f(int i, int j) {
	if (j > i) return 1;
	else if (i%j == 0) return i / j;
	else return i / j + 1;
}
int main() {
	cin >> n >> m;
	rep(i, m) cin >> A[i] >> B[i];
	rep(i, m) {
		C[i] = (f(n, A[i]))*B[i];
	}
	sort(C, C + m);
	cout << C[0] << endl;
	return 0;
}
