#define pp pair<ll,ll>
#define ppp pair<ll,pp>
#define fi first
#define se second
#define pb push_back
#define fix(n) fixed<<setprecision(n)
#define itr vector<ll>::iterator
#define print(s) cout<<s<<endl
#define inp(s) cin>>s
ll n, m,v[60010],ans;
pp a[60010];
int main() {
	cin >> n >> m;
	rep(i, n) { cin >> v[i]; a[i] = pp{ v[i],i + 1 }; }
	sort(a, a + n);
	rep(i, n) {
		if (m >= a[i].fi*a[i].se) { ans += a[i].se; m -= a[i].fi*a[i].se; }
		else { ans += m / a[i].fi; break; }
	}
	cout << ans << endl;
	return 0;
}
