#include <iostream>
#include <string>
int main(){
	int b=0,w=0;
	std::string str;
	std::cin>>str;
	for(int i=0;i<100;i++){
		if(str[i]=='B'){
			b+=1;
		}
		if(str[i]=='W'){
			w+=1;
		}
	}
	if(b>w){
		std::cout<<"Black wins"<<std::endl;
	}
	if(b<w){
		std::cout<<"White wins"<<std::endl;
	}
	if(b==w){
		std::cout<<"Draw"<<std::endl;
	}
	return 0;
}
