#include <iostream>
#include <algorithm>
#include <string>
#include <vector>

using namespace std;

int main() {
	int t=0, e=0;
	string s;
	cin >> s;
	int y = s.size();
	for (int i = 0;i < y;i++) {
		if (s[i] != 'B'&& s[i]!='E') {
			e++;
		}
		else if (s[i] != 'W' && s[i]!='E') {
			t++;
		}
		else {
			continue;
		}
	}
	int m = max(t, e);
	if (m ==t && m!=e) {
		cout << "Black wins" << endl;
	}
	else if(m==e && m!=t) {
		cout << "White wins" << endl;
	}
	else if (t == e) {
		cout << "Draw" << endl;
	}
}
