#include<iostream>
#include<string>

int main() {
	int N, M;
	std::cin >> N >> M;
	int amount[10000], price[10000], kprice = 1145141919;

	for (int i = 0; i < M; i++) {
		int k = 0;
		std::cin >> amount[i] >> price[i];
		for (int n = N; n > 0; n -= amount[i]) {
			k += price[i];
		}
		if (kprice > k)kprice = k;
	}
	std::cout << kprice << std::endl;
	return 0;
}
