#include <iostream>
#include <string>
int main(){
    int b=0,w=0;
    std::string a;
    std::cin>>a;
    for(int i=0;i<a.size();++i){
        if(a[i]=='B'){
            ++b;
        }
        if(a[i]=='W'){
            ++w;
        }
   }
    if(b<w){
        std::cout<<"White wins"<<std::endl;
   }
    if(b>w){
        std::cout<<"Black wins"<<std::endl;
   }
    if(b==w){
        std::cout<<"Draw"<<std::endl;
   }
}
