#include <iostream>
#include <vector>
#include <string>
using namespace std;

int main() {

	string str;
	
	cin >> str;

	int a= str.length();
	int w = 0; 
	int b = 0;

	for (int i = 1; i <= a; i++) {
		if (str.substr(i, 1)=="W") {
			w++;
		}
		if (str.substr(i, 1) == "B") {
			b++;
		}
	}
	if (w == b) {
		cout << "Draw" << endl;
	}
	else if (w > b) {
		cout << "White wins" << endl;
	}
	else {
		cout << "Black wins" << endl;
	}
}
