#include <iostream>
#include <vector>
#include <string>
using namespace std;
int main(void){
  
    int N,M;
    cin>>N>>M;
    vector<int> A(M);
    vector<int> B(M);
    int min = 10001;
    for(int i=0;i<A.size();i++){
        cin>>A[i]>>B[i];
        if(B[i]/A[i]<min){
            min = (N/A[i]+(N%A[i]>0?1:0))*B[i];
        }
    }
    std::cout<<min<<std::endl;
}
