#include<iostream>
#include<string>

int main() {
	int n;
	int m;
	std::cin >> n;
	std::cin >> m;
	int a;
	int b;
	int ans =100000000 ;
	for (int i = 0; i < m; i++) {
		std::cin >> a;
		std::cin >> b;
		if (n%a == 0) {
			if (ans > b*(n / a)) {
				ans = b*(n / a);
			}
		}
		else{
			if (ans > b*((n / a)+1)) {
				ans = b*((n / a)+1);
			}
		}
	}
	std::cout << ans << std::endl;
}
