#include<iostream>
#include<string>

int n,m,a,b;

float now=10000000000;

int main(){
    
    std::cin>>n>>m;
    
    for(int i=0;i<m;i++){
        std::cin>>a>>b;
        if(b/a<now)
            now=b/a;
    }
    
    std::cout<<(int)now*n<<std::endl;
    
    return 0;
    
}
