#include <iostream>
#include <string>
int main() {
	std::string str;
	std::cin >> str;
	int b = 0, w = 0;
	int bj = 0, bj2 = 0;
	int wj = 0, wj2 = 0;
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'B')
			bj += 1;
	}
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'B' && bj - 1 > 0)
			bj2 = 1, bj -= 1;
		else if (bj - 1 <= 0)
			bj2 = 0;
		if (str[i] == 'W' && bj2 == 1)
			str[i] = 'B';
			
	}
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'W')
			wj += 1;
	}
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'W' && wj - 1 > 0)
			wj2 = 1, wj -= 1;
		else if (wj - 1 <= 0)
			wj2 = 0;
		if (str[i] == 'B' && wj2 == 1)
			str[i] = 'W';

	}
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'B')
			++b;
		 if (str[i] == 'W')
			++w;
	}
	if (b > w)
		std::cout << "Black wins" << std::endl;
	else if (b < w)
		std::cout << "White wins" << std::endl;
	else
		std::cout << "Draw" << std::endl;
	system("pause");
}
