#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

string s[100010];
ll n = -1;

int main() {
	int cnt = 0;
	while (cin >> s[cnt]) {
		cnt++;
	}
	for (int i = 0;i < cnt;i++) {
		if (s[i].size() != 6)continue;
		for (int j = 0;j <26;j++) {
			string r;
			for (int k = 0;k < s[i].size();k++) {
				r += (s[i][k] - 'a' + j) % 26 + 'a';
			}
			if (r == "caesar") {
				n = j;
				break;
			}
		}
		if (n != -1) {
			break;
		}
	}
	for (int i = 0;i < cnt;i++) {
		for (int j = 0;j < s[i].size();j++) {
			bool f=0;
			for (int k = 0;k < 26;k++) {
				if (s[i][j] == 'a' + k)f = 1;
			}
			if (f) {
				s[i][j] = (s[i][j] - 'a' + n) % 26 + 'a';
			}
		}
		if (i != cnt - 1)
			cout << s[i] << ' ';
		else
			cout << s[i] << endl;
	}
	return 0;
}
