#include <iostream>
#include <string>
#include <vector>

int main() {
	int X;
	std::cin >> X;
	unsigned long long int kakko = 2;
	for (int i = 2; i <= X; i++) {
		kakko = kakko * 2;
	}
	kakko = kakko - 1;
	unsigned long long int aaa = kakko % 1000000007;
	std::cout << aaa << std::endl;
	return 0;
}
