#include <iostream>
#include <algorithm>
#include <limits>

constexpr int INF = std::numeric_limits<int>::max();
constexpr int MAX_N = 50;
constexpr int MAX_L = 1000;

int main(){

	int N, L[MAX_N];

	std::cin >> N;

	for(int i = 0; i < N; ++i){
		std::cin >> L[i];
	}

	int dp[MAX_N][MAX_N][2];  // 0: Max, 1: min

	for(int i = 0; i < N; ++i){
		dp[i][i][0] = dp[i][i][1] = L[i];
	}

	for (int i = 0; i < N; ++i){
		for(int j = i + 1; j < N; ++j){

			int diff = INF;
			int max = 0, min = INF;

			for(int k = i; k < j; ++k){

				int cmax = std::max(dp[i][k][0], dp[k + 1][j][0]);
				int cmin = std::min(dp[i][k][1], dp[k + 1][j][1]);

				if (cmax - cmin < diff){
					diff = cmax - cmin;
					max = cmax;
					min = cmin;
				}
			}

			dp[i][j][0] = max, dp[i][j][1] = min;
		}
	}

	std::cout << dp[0][N - 1][0] - dp[0][N - 1][1] << std::endl;

	return 0;
}
