#include<iostream>
#include<vector>
#include<algorithm>
#include<functional>
#include<map>
#include<string>
#include<cmath>
#include<utility>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
typedef long long ll;

int main() {
	int n = 0, m = 0;
	int count = 0,money = 0;
	std::map<int, int> map;
	int tanka[10000] = {};
	int a[10000] = {}, b[10000] = {};
	std::cin >> n >> m;

	rep(i, m) {
		std::cin >> a[i] >> b[i];
	}
	rep(i, m) {
		tanka[i] = b[i] / a[i];
	}
	std::sort(tanka, tanka + m, std::greater<int>());
	std::cout << tanka[m-1] << std::endl;
	while (count < n) {
		count++;
		money += tanka[m-1];
	}
	std::cout << money << std::endl;
	return 0;
}
