#include <iostream>
#include <string>
	int main() {
		std::string str;
		std::cin >> str;
		int a = str.size();
		for (int i = 0; i<a; i++) {
			char c = str[i];
			if (c <= 'w'&&c >= 'a') {
				c += 3;
				std::cout << c;
			}
			if (c >= 'x'&&c <= 'z') {
				c -= 23;
				std::cout << c;
			}

		}
		std::cout << std::endl;
			}
