#include<iostream>
int main(){
int n,m,a[10000],b[10000],A[10000],B=0;
std::cin>>n>>m;
for(int i=0;i<m;i++){
std::cin>>a[i]>>b[i];
if(n%a[i]==0)
A[i]=n/a[i]*b[i];
else
A[i]=(n/a[i])*b[i]+b[i];
}
for(int i=0;i<m;i++){
if(A[i]<=A[i+1])
B=A[i];
else
B=A[i+1];
}
std::cout<<B<<std::endl;
return 0;
}
