#include<iostream>
#include<string>
int main() {
	int sum[2];
	std::string x;
	std::cin >> x;
	sum[0] = 0; sum[1] = 0;
	for (int i = 0; i < x.size(); i++) {
		if (x[i] == 'B') {
			sum[0] += 1;
		}
		if (x[i] == 'W') {
			sum[1] += 1;
		}
	}
	if (sum[0] > sum[1]) {
		std::cout << "Black wins" << std::endl;
	}
	if (sum[0] < sum[1]) {
		std::cout << "White wins" << std::endl;
	}
	if (sum[0] == sum[1]) {
		std::cout << "Draw" << std::endl;
	}
}
