#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>

int main(int argc, char **argv)
{
	std::string input;
	std::getline(std::cin, input);
	for(int i = 0; i < 26; i++){
		for(int j = 0; j < input.size(); j++){
			if(input[j] == 'a'){
				input[j] = 'z';
			}else if(input[j] >= 'b' && input[j] <= 'z'){
				input[j]--;
			}
		}
		if((int)input.find("caesar") > -1){
			break;
		}
	}
	std::cout << input << std::endl;
	return 0;
}
