#include<iostream>
#include<string>
#include<cmath>

float n,m,a,b;

int res=1000000000;

int main(){
    
    std::cin>>n>>m;
    
    for(int i=0;i<m;i++){
        std::cin>>a>>b;
        if(res>ceil(m/a)*b)
            res=ceil(m/a)*b;
    }
    
    std::cout<<res<<std::endl;
    
    return 0;
    
}
