#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second

ll n, m, sum = 0, ans = 0;
P a[100010];

int main() {
	cin >> n >> m;
	for (int i = 1;i <= n;i++) {
		cin >> a[i].fr;
		a[i].sc = i;
	}
	sort(a + 1, a + n + 1);
	for (int i = 1;i <= n;i++) {
		for (int j = 0;j < a[i].sc;j++) {
			sum += a[i].fr;
			if (sum > m) {
				cout << ans << endl;
				return 0;
			}
			ans++;
		}
	}
	cout << ans << endl;
	return 0;
}
