#include<iostream>
#include<string>
#include<cmath>

float n,m,a,b;

long int res=100000000000;

int main(){
    
    std::cin>>n>>m;
    
    for(int i=0;i<m;i++){
        std::cin>>a>>b;
        if(res>ceil(n/a)*b)
            res=ceil(n/a)*b;
    }
    
    std::cout<<res<<std::endl;
    
    return 0;
    
}
